				if(prefix.isEmpty()){
					listOfKeys<<key
				}else{
					listOfKeys<< prefix+"."+key
				}

				if (value instanceof List)
				{
					value.eachWithIndex { item, index->  item.each{k,v-> listOfKeys<<k

							if (v instanceof List)
							{
								v.eachWithIndex { item1, index1->  item1.each{k1,v1-> listOfKeys<<k1}}

							}
							else if(v instanceof Map){
								v.each{k1,v1-> listOfKeys<<k1

								}

							}

						}}

				}
				else if(value instanceof Map){

					value.each{k,v-> listOfKeys<<k
