	def getOptionsCategoryId(Map map, String prefix=''){
				def OptionsCategoryIds=[]
				if(map instanceof Map){

					map.each { key, value->
						if (key=="options"){

							if(value instanceof Map){

								value.each{k,v->
									if(v instanceof Map){
										v.each{k1,v1->
											if(v1 instanceof Map){
												v1.each{k122,v122->
													if(v122 instanceof Map){

														OptionsCategoryIds<<v122.categoryId.toString()
														//v122.each{k15,v15-> listOfKeys<<k15+':'+v15}

													}

												}

											}
										}

									}

								}

							}
						}
					}

				}
				OptionsCategoryIds
			}
    }

